"use strict";
Object.prototype.animate=function(a,b=null,c={easingFunc,duration,fps}){c.easingFunc=c.easingFunc||'linear';c.duration=c.duration||1000;c.fps=c.fps||50;var d=this,e=1000/c.fps,f=c.duration/e,g=getComputedStyle(d),j=new Object,k=new Object;for(var l in a){if(a.hasOwnProperty(l)){j[l]=parseFloat(g[l]);k[l]=(parseFloat(a[l])-j[l])/f;}}
    var m=1,n=setInterval(function(){if(m>=f+1){clearInterval(n);if(b){b();}}else{switch(c.easingFunc){case 'linear':for(var o in k){if(k.hasOwnProperty(o)){d.style[o]=(j[o]+m*k[o])+"px";}}break;default:c.easingFunc();break;}m++;}},e);}

// Получение ссылки на объект с именем HTML-тега "а"
document.querySelector('span')
    // Назначение обработчика на событие "клик по элементу"
    .addEventListener("click", function(event){
        // Отмена действия по умолчанию браузера на событие
        event.preventDefault();
        // вызов метода анимации на целевом объекте
        event.target.animate({width:"350px", height:"200px"}, function(){
            console.log('Анимация завершена!');
        }, {duration:50});
    });