// Явное указание на режим строгого соответствия современному стандарту
"use strict";
/* ================================================= *
 * ==== Решение "disableScroll.js" ================= *
 * ==== Блокировка/Активация прокрутки страницы ==== *
 * ================================================= */
var keys={32:1,33:1,34:1,35:1,36:1,37:1,38:1,39:1,40:1};function preventDefaultForScrollKeys(e){if(keys[e.keyCode]){e.preventDefault();return false;}}function disableScroll(){if(window.addEventListener){window.addEventListener('DOMMouseScroll',function(e){e.preventDefault();},false);}window.onwheel=function(e){e.preventDefault();};window.onmousewheel=document.onmousewheel=function(e){e.preventDefault();};window.ontouchmove=function(e){e.preventDefault();};document.onkeydown=preventDefaultForScrollKeys;}function enableScroll(){if(window.removeEventListener){window.removeEventListener('DOMMouseScroll',function(e){e.preventDefault();},false);}window.onwheel=null;window.onmousewheel=document.onmousewheel=null;window.ontouchmove=null;document.onkeydown=null;}
