"use strict";
/* ============================================== *
 * ==== Решение "Плавная прогрутка до якоря" ==== *
 * ============================================== */
function scrollingToAnchor(e,c=null){var a=e.target.getAttribute('href'),b=document.getElementById(a.match(/[^#].*/)).offsetTop,d=75,k=setInterval(function(){var f=window.pageYOffset,g=(b-f),j=(Math.abs(g)!=2)?g/3:g;if(g==0){clearInterval(k);if(c){c();}}else{window.scrollBy(0,j);}},1000/d);};
/* ============================================== */