"use strict";

/* ============================================== *
 * ==== Решение "Плавная прогрутка до якоря" ==== *
 * ============================================== */
function scrollingToAnchor(e){var a=e.target.getAttribute('href'),b=document.getElementById(a.match(/[^#].*/)).offsetTop,c=75,d=setInterval(function(){var e=window.pageYOffset,f=(b-e),g=(Math.abs(f)!=2)?f/3:f;if(f==0){clearInterval(d);return true;}else{window.scrollBy(0,g);}},1000/c);return false;};