// Явное указание на режим строгого соответствия современному стандарту
"use strict";

/* ============================================== *
 * ==== Решение "Плавная прогрутка до якоря" ==== *
 * ============================================== */
function smoothScrolling(event){var a=event.target.getAttribute('href'),b=document.getElementById( a.match(/[^#].*/) ).offsetTop,i=1000/100,s=setInterval(function(){var c=window.pageYOffset,d=(b-c),e=(Math.abs(d)!=2)?d/3:d;if(d==0){clearInterval(s);}else{window.scrollBy(0,e);}},i);return false;};