function getScrollBarWidth(){var a=document.createElement("div");a.style.position="absolute";a.style.bottom="0px";a.style.width="50px";a.style.height="50px";a.style.overflowY="scroll";a.style.visibility="hidden";document.body.appendChild(a);var b=a.offsetWidth-a.clientWidth;document.body.removeChild(a);return b}var scrollbarWidth=getScrollBarWidth();